function mapWithSeparator(
  items,
  itemRenderer,
  spacerRenderer,
) {
  const mapped = [];
  if (items.length > 0) {
    mapped.push(itemRenderer(items[0], 0, items));
    for (let ii = 1; ii < items.length; ii++) {
